create procedure DBA.PU_DIAMESANO(in P_MES integer,in P_ANO integer,out P_PDIA integer,out P_UDIA integer)
begin
  declare DATAINIMES date;
  declare PANO integer;
  declare PMES integer;
  set PANO=P_ANO;
  set PMES=P_MES+1;
  if PMES = 13 and P_MES <> 0 then
    set PMES=1;
    set PANO=PANO+1
  end if;
  if P_MES = 0 then
    set PMES=1;
    set PANO=PANO+1;
    set P_MES=1
  end if;
  execute immediate 'SET DATAINIMES=''' || PANO || '-' || STR_ZERO(PMES,2) || '-01''';
  set DATAINIMES=DATAINIMES-1;
  execute immediate 'SET P_PDIA= (SELECT DATEDIFF(DAY,''1800-12-28'',''' || P_ANO || '-' || P_MES || '-01''))';
  execute immediate 'SET P_UDIA= (SELECT DATEDIFF(DAY,''1800-12-28'',''' || DATAINIMES || '''))'
end
go

